import { Form, Link, useLocation, useNavigate } from 'react-router-dom';
import Dropdown from 'react-bootstrap/Dropdown';
import { useLogin } from '../js/LoginHandler';
import '../css/admin.css';
import { useState, useEffect } from 'react';
import $ from 'jquery'


import Menu from "../component/menu";



const Admin = () => {
    const navigate = useNavigate()

    const [categories, setCategories] = useState([]);
    const [locations, setLocations] = useState([]);
    const [accomodations, setAccomodations] = useState([]);
    const [places, setPlaces] = useState([]);
    const [transportations, setTransportations] = useState([]);


    function getCategories() {
        $.ajax({
            url: 'http://127.0.0.1:5000/category',
            type: 'GET',
            dataType: 'json',
            success: function (response) {
                // Update state with fetched data
                setCategories(response.categories);
                // if (response) console.log(response.categories)
            },
            error: function (error) {
                console.error('Error:', error);
            },
        });
    }

    function getLocations() {
        $.ajax({
            url: 'http://127.0.0.1:5000/province',
            type: 'GET',
            dataType: 'json',
            success: function (response) {
                // Update state with fetched data
                setLocations(response.provinces);
                // if (response) console.log(response.categories)
            },
            error: function (error) {
                console.error('Error:', error);
            },
        });
    }

    function getPlaces() {
        $.ajax({
            url: 'http://127.0.0.1:5000/place',
            type: 'GET',
            dataType: 'json',
            success: function (response) {
                // Update state with fetched data
                setPlaces(response.places);
                // if (response) console.log(response.categories)
            },
            error: function (error) {
                console.error('Error:', error);
            },
        });
    }

    function getAccomodations() {
        $.ajax({
            url: 'http://127.0.0.1:5000/accomodation',
            type: 'GET',
            dataType: 'json',
            success: function (response) {
                // Update state with fetched data
                setAccomodations(response.accomodations);
                // if (response) console.log(response.categories)
            },
            error: function (error) {
                console.error('Error:', error);
            },
        });
    }

    function getTransportations() {
        $.ajax({
            url: 'http://127.0.0.1:5000/transportation',
            type: 'GET',
            dataType: 'json',
            success: function (response) {
                // Update state with fetched data
                setTransportations(response.transportations);
                // if (response) console.log(response.categories)
            },
            error: function (error) {
                console.error('Error:', error);
            },
        });
    }

    async function create(table, data) {
        try {
            const response = await $.ajax({
                url: `http://127.0.0.1:5000/${table}/create`,
                type: 'POST',
                dataType: 'json',
                contentType: 'application/json',
                data: JSON.stringify(data),
            });

            if (response.status) {
                window.location.reload();
            }
            return response.status
        } catch (error) {
            console.error('Something Went Wrong:', error);
            // setIsLoggedIn(false);
        }
    }
    async function write(table, data) {
        try {
            const response = await $.ajax({
                url: `http://127.0.0.1:5000/${table}/write`,
                type: 'POST',
                dataType: 'json',
                contentType: 'application/json',
                data: JSON.stringify(data),
            });

            if (response.status) {
                window.location.reload();
            }
            return response.status
        } catch (error) {
            console.error('Something Went Wrong:', error);
            // setIsLoggedIn(false);
        }
    }
    async function unlink(table, data) {
        try {
            const response = await $.ajax({
                url: `http://127.0.0.1:5000/${table}/unlink`,
                type: 'POST',
                dataType: 'json',
                contentType: 'application/json',
                data: JSON.stringify(data),
            });

            if (response.status) {
                window.location.reload();
            }
            return response.status
        } catch (error) {
            console.error('Something Went Wrong:', error);
            // setIsLoggedIn(false);
        }
    }

    function handleEditCategory(e) {
        e.preventDefault()

        var dataset = e.target.dataset;
        var $form = $('.category-form')

        $form.find('#id').val(dataset.id);
        $form.find('#name').val(dataset.name);
        $form.find('#detail').val(dataset.detail);
        $form.find('#image').val(dataset.image);
    }
    function submitCreateEditCategory(e) {
        e.preventDefault()
        var $form = $('.category-form')
        const isCreate = $form.find('#id').val() ? false: true
        var data = {
            name: $form.find('#name').val(),
            detail: $form.find('#detail').val(),
            image: "",
            image_url: $form.find('#image').val()
        }
        if (isCreate) {
            create('category', data)
        } else {
            data['id'] = $form.find('#id').val()
            write('category', data)
        }
    }
    function handleDeleteCategory(e) {
        e.preventDefault()
        const data = {
            id: e.target.dataset.id
        }
        unlink('category', data)

    }
    function submitCreateEditlocation(e) {
        e.preventDefault()
        var $form = $('.location-form')
        const isCreate = $form.find('#id').val() ? false: true
        var data = {
            name: $form.find('#name').val(),            
        }
        if (isCreate) {
            create('province', data)
        } else {
            data['id'] = $form.find('#id').val()
            write('province', data)
        }
    }
    function handleDeleteLocation(e) {
        e.preventDefault()
        const data = {
            id: e.target.dataset.id
        }
        unlink('province', data)

    }
    function handleEditlocation(e) {
        e.preventDefault()

        var dataset = e.target.dataset;
        var $form = $('.location-form')

        $form.find('#id').val(dataset.id);
        $form.find('#name').val(dataset.name);

    }

    // ===Place===
    function handleEditPlace(e) {
        e.preventDefault()

        var dataset = e.target.dataset;
        var $form = $('.place-form')

        $form.find('#id').val(dataset.id);
        $form.find('#name').val(dataset.name);
        $form.find('#price').val(dataset.price);
        $form.find('#province_id').val(dataset.province_id);
        $form.find('#detail').val(dataset.detail);
        $form.find('#image').val(dataset.image);
    }
    function submitCreateEditPlace(e) {
        e.preventDefault()
        var $form = $('.place-form')
        const isCreate = $form.find('#id').val() ? false: true
        var data = {
            name: $form.find('#name').val(),
            price: $form.find('#price').val(),
            province_id: $form.find('#province_id').val(),
            detail: $form.find('#detail').val(),
            image: "",
            image_url: $form.find('#image').val()
        }
        if (isCreate) {
            create('place', data)
        } else {
            data['id'] = $form.find('#id').val()
            write('place', data)
        }
    }
    function handleDeletePlace(e) {
        e.preventDefault()
        const data = {
            id: e.target.dataset.id
        }
        unlink('place', data)

    }

    // ===Accommodations===
    function handleEditAccommodation(e) {
        e.preventDefault()

        var dataset = e.target.dataset;
        var $form = $('.accommodation-form')

        $form.find('#id').val(dataset.id);
        $form.find('#name').val(dataset.name);
        $form.find('#price').val(dataset.price);
        $form.find('#province_id').val(dataset.province_id);
        $form.find('#detail').val(dataset.detail);
        $form.find('#image').val(dataset.image);
    }
    function submitCreateEditAccommodation(e) {
        e.preventDefault()
        var $form = $('.accommodation-form')
        const isCreate = $form.find('#id').val() ? false: true
