import React, { useState, useEffect } from 'react';
import '../css/category.css';
import landscape1 from '../img/landscape1.png';
import Slider from "react-slick";


import $ from 'jquery'


const getSliderSettings = () => {
  return {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };
};

const Category = () => {

    const [categories, setCategories] = useState([]);

    function getCategories() {
        $.ajax({
            url: 'http://127.0.0.1:5000/category',
            type: 'GET',
            dataType: 'json',
            success: function (response) {
                // Update state with fetched data
                setCategories(response.categories);
                // if (response) console.log(response.categories)
            },
            error: function (error) {
                console.error('Error:', error);
            },
        });
    }

    // Use useEffect to call the function when the component mounts
    useEffect(() => {
        getCategories();
    }, []); // Empty dependency array ensures it only runs once

    const settings = getSliderSettings();

    return (
        <article>
            <Slider {...settings}>
                {/* <div className="nest-beach-boxshow">
                    <div>
                        <h4 className="main-caption caption-category">Category</h4>
                    </div>
                    <div className="show-category">
                        {categories.map((category) => (
                            <div className="card" style={{ width: '18rem' }} key={category.id}>
                                <img src={category.image_url || category.image || landscape1} className="card-img-top" alt="..." />
                                <div className="card-body">
                                    <h5 className="card-title">{category.name}</h5>
                                    <p className="card-text">{category.detail}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div> */}
                <div>
                <h3>1</h3>
                </div>
                <div>
                <h3>2</h3>
                </div>
                <div>
                <h3>3</h3>
                </div>
                <div>
                <h3>4</h3>
                </div>
                <div>
                <h3>5</h3>
                </div>
                <div>
                <h3>6</h3>
                </div>
                <div>
                <h3>7</h3>
                </div>
                <div>
                <h3>8</h3>
                </div>
            </Slider>
        </article>
    );
};

export default Category;
